mapper.select:
select member_no as memberNo, member_name as memberName, member_id as memberId, member_pw as memberPw, member_bday as memberBdate, member_phone as memberPhone, member_addr as memberAddr, member_gender as memberGender, enroll_date as enrollDate from member_tbl where member_id=#{memberId}
and member_pw=#{memberPw}
and member_addr=#{memberAddr}

mapper.update:
update member_tbl
set member_pw = #{memberPw}
where member_id = #{memberId}

mapper.select:
select petsitter_board_no as petsitterBoardNo
from partner_petsitter_board
where p_no=#{_parameter}
