mapper.insert:
insert into review_tbl values( 'REVIEW-' || review_seq.nextval, #{bookingNo}, #{memberNo}, #{pNo}, #{petNo}, #{reviewRate}, #{reviewContent}, to_char(sysdate,'yy/mm/dd'),
#{reviewFilename}, #{reviewFilepath}
null, null
)

mapper.update:
update review_tbl set
	review_rate = #{reviewRate},
	review_content = #{reviewContent},
	review_filename = #{reviewFilename},
	review_filepath = #{reviewFilepath}
where booking_no=#{bookingNo}
